<script>
jQuery(function(){
    var len = jQuery(".sideMainBox .slidernav > li").length,
        index = 1,
        intervaltime;

    function switchPicA(num){
        jQuery(".sideMainBox .slidernav > li").removeClass("current").eq(num).addClass("current");
        jQuery(".sideMainBox .sliderbox").find("li:lt(5)").removeClass("disappear").addClass("appear");
    }
    function switchPicB(num){
        jQuery(".sideMainBox .slidernav > li").removeClass("current").eq(num).addClass("current");
        jQuery(".sideMainBox .sliderbox").find("li:lt(5)").removeClass("appear").addClass("disappear");
    }
    function auto(){
        if(index == len){
            index = 0;
        }
        if(index == 0){
            switchPicA(index);
        }else if(index == 1){
            switchPicB(index);
        }
        index = index+1;
    }
    intervaltime = setInterval(auto, 5000);

    jQuery(".sideMainBox .slidernav > li").mouseover(function(){
        var index_num = jQuery(".sideMainBox .slidernav > li").index(this);
        if(index_num == 0){
            switchPicA(index_num);
        }else if(index_num == 1){
            switchPicB(index_num);
        }
        index = index_num + 1;

    });
    jQuery(".sideMainBox").mouseover(function(){
        clearInterval(intervaltime);
    });
    });
</script> 

